import React, { useEffect, useState } from 'react';
import axios from 'axios';

function App() {
  const [students, setStudents] = useState([]);
  const [form, setForm] = useState({
    name: '',
    email: '',
    course: '',
    deliveryMode: 'Weekday',
    lectureMode: 'Online'
  });
  const [editId, setEditId] = useState(null); // for editing

  useEffect(() => {
    fetchStudents();
  }, []);

  const fetchStudents = async () => {
    const res = await axios.get('http://localhost:5000/students');
    setStudents(res.data);
  };

  const handleSubmit = async () => {
    if (editId) {
      await axios.put(`http://localhost:5000/students/${editId}`, form);
      setEditId(null);
    } else {
      await axios.post('http://localhost:5000/students', form);
    }
    setForm({
      name: '',
      email: '',
      course: '',
      deliveryMode: 'Weekday',
      lectureMode: 'Online'
    });
    fetchStudents();
  };

  const handleDelete = async (id) => {
    if (window.confirm("Are you sure you want to delete this student?")) {
      await axios.delete(`http://localhost:5000/students/${id}`);
      fetchStudents();
    }
  };

  const handleEdit = (student) => {
    setForm({
      name: student.name,
      email: student.email,
      course: student.course,
      deliveryMode: student.deliveryMode,
      lectureMode: student.lectureMode
    });
    setEditId(student._id);
  };

  return (
    <div className="container mt-5">
      <h2 className="mb-4 text-primary">ðŸ“š Student Registration</h2>

      <div className="card p-4 mb-4 shadow-sm">
        <div className="row g-3">
          <div className="col-md-4">
            <input
              className="form-control"
              placeholder="Name"
              value={form.name}
              onChange={e => setForm({ ...form, name: e.target.value })}
            />
          </div>
          <div className="col-md-4">
            <input
              className="form-control"
              placeholder="Email"
              value={form.email}
              onChange={e => setForm({ ...form, email: e.target.value })}
            />
          </div>
          <div className="col-md-4">
            <input
              className="form-control"
              placeholder="Course Name"
              value={form.course}
              onChange={e => setForm({ ...form, course: e.target.value })}
            />
          </div>
          <div className="col-md-6">
            <label className="form-label">Delivery Mode</label>
            <select
              className="form-select"
              value={form.deliveryMode}
              onChange={e => setForm({ ...form, deliveryMode: e.target.value })}
            >
              <option value="Weekday">Weekday</option>
              <option value="Weekend">Weekend</option>
            </select>
          </div>
          <div className="col-md-6">
            <label className="form-label">Lecture Mode</label>
            <select
              className="form-select"
              value={form.lectureMode}
              onChange={e => setForm({ ...form, lectureMode: e.target.value })}
            >
              <option value="Online">Online</option>
              <option value="Onsite">Onsite</option>
            </select>
          </div>
          <div className="col-12 text-end mt-3">
            <button className="btn btn-success" onClick={handleSubmit}>
              {editId ? "Update Student" : "Submit"}
            </button>
          </div>
        </div>
      </div>

      <h4 className="text-secondary">ðŸ“‹ Registered Students</h4>
      <table className="table table-bordered table-striped">
        <thead className="table-light">
          <tr>
            <th>Name</th>
            <th>Email</th>
            <th>Course</th>
            <th>Delivery Mode</th>
            <th>Lecture Mode</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {students.map(s => (
            <tr key={s._id}>
              <td>{s.name}</td>
              <td>{s.email}</td>
              <td>{s.course}</td>
              <td>{s.deliveryMode}</td>
              <td>{s.lectureMode}</td>
              <td>
                <button className="btn btn-warning btn-sm me-2" onClick={() => handleEdit(s)}>Edit</button>
                <button className="btn btn-danger btn-sm" onClick={() => handleDelete(s._id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
